)","library(dplyr)","library(flextable)","library(readxl)","","# j'ai ajoute cette ligne pour que le fichier MCC soit chargé, si vous avez","# decomposé votre code avec server, ui, global, il suffit de la mettre dans","# global.R","source(\"MCC_24-25.R\")","","","# renvoie les notes de l'élève nom prenom du semestre associé à la feuille de l'excel fichier","notes_from_jury \u003c- function(fichier, feuille, colonnes, id){","  semestre \u003c- read_excel(fichier, sheet = feuille, col_types = \"text\")","  notes \u003c- semestre[semestre[[1]] == id, colonnes]","  return(unlist(notes[rowSums(is.na(notes)) != ncol(notes), ]))","}","","#renvoie les validations ou non de chaque UE pour un semestre","valide_UE \u003c- function(fichier, feuille, col_val, id){","  semestre \u003c- read_excel(fichier, sheet = feuille, col_types = \"text\")","  ","  validations \u003c- semestre[semestre[[1]] == id, col_val]","  return(unlist(validations[rowSums(is.na(validations)) != ncol(validations), ]))","}","","# renvoie un vecteur contenant des croix si l'ec n'est pas validé","croix \u003c- function(notes,validation){","  croix \u003c- c()","  i \u003c- 1","  for (j in 1:length(validation)){","    if (validation[j] == \"NON VALIDE\"){","      while(i \u003c= nb_EC_UE[j]){","        if (as.integer(notes[i]) \u003c 10 || is.na(as.integer(notes[i]))){","          croix[i] \u003c- \"X\"","        }else{","          croix[i] \u003c- \"\"","        }","        i = i+1","      }","    }else{","      while(i \u003c= nb_EC_UE[j]){","        croix[i] \u003c- \"\"","        i = i+1","      }","    }","    }","  return(croix)","}","","","# création des tableaux de données","build_notes_etudiant \u003c- function(notes, croix){","  return(","    data.frame(","      LesUE[1],","      LesUE[2],","      LesUE[3],","      Moyennes = notes,","      EcValRepasse = c(rep(\"\",length(LesUE[1]))),","      EcAVal = croix","    ))","}","","build_signature \u003c- function(){","  return(","      data.frame(","      c1 = c(\"Date :\\n\\n\\n\\n\\n\"),","      c2 = c(\"Date :\\n\\n\\n\\n\\n\"),","      c3 = c(\"\\n\\n\\n\\n\\n \")","    )","  )","}","","","#flextable avec la signature à la fin du document word","build_ft_signature \u003c- function(sign){","  return(","    sign %\u003e%","      bold(part = \"header\")%\u003e%","      set_header_labels(","        c1 = \"Signature de l'étudiant·e précédée de la mention «lu et approuvé»\",","        c2 = direction_departement,","        c3 = \"Cachet de l'établissement\"","      )%\u003e%","      width(j = c(1,2,3), width = c(2.5,2.5,2.5))%\u003e%","      border_outer(border = fp_border(color = \"black\", width = 1, style = \"solid\")) %\u003e%","      border_inner_h(border = fp_border(color = \"grey\", width = 0.5, style = \"solid\")) %\u003e%","      border_inner_v(border = fp_border(color = \"grey\", width = 0.5, style = \"solid\"))","  )","}","","# concaténation de lignes pour avoir 1 case par UE","merge_UE \u003c- function(ft){","  ft \u003c- ft %\u003e% merge_at(i = 1:nb_EC_UE[1], j = 1)","  for (c in 1:(length(nb_EC_UE)-1)) {","    ft \u003c- ft %\u003e% merge_at(i = (nb_EC_UE[c]+1):nb_EC_UE[c+1], j = 1)","  }","  return(ft)","}","","","","#flextable avec ue, ec, code EC, moyenne, à repasser, à valider","build_ft_notes \u003c- function(ft){","    ft \u003c- ft %\u003e%","      bold(part = \"header\")%\u003e%","      ","      #p changement des noms des colonnes","      set_header_labels(","        Ue = \"UE (code UE)\",","        Ec = \"EC\",","        CodeEC = \"Code EC\",","        Moyennes = paste(\"EC \", annee_courante, \" (Moyenne obtenue)\"),","        EcValRepasse = paste(\"EC validé mais repassé en \", annee_suivante),","        EcAVal = paste(\"EC à valider en \", annee_suivante)","      )%\u003e%","      ","      # choix de la largeur des colonnes","      width(j = c(1,2,3,4,5), width = c(1.5,1.7,1.5,1,1))%\u003e%","      ","      # pour mettre les bords en gras","      border_outer(border = fp_border(color = \"black\", width = 1, style = \"solid\"))%\u003e%","      ","      # pour mettre les lignes horizontales et verticales en gris pour pouvoir les visialiser sur document imprimé","      border_inner_h(border = fp_border(color = \"grey\", width = 0.5, style = \"solid\")) %\u003e%","      border_inner_v(border = fp_border(color = \"grey\", width = 0.5, style = \"solid\")) %\u003e%","      ","      # pour mettre la ligne horizontale qui sépare le S3 du S4 en gras","      hline(i = nb_EC_S3, border = fp_border(width = 1.5, color = \"black\"))","    ","    # concaténation de lignes pour avoir 1 case par UE","    ft \u003c- ft %\u003e% merge_UE()","  return (ft)","}","","","# choix de la police, couleur, taille du titre","titre \u003c- function(){","  return(","    fpar(","      ftext(\"CONTRAT D'ÉTUDES 2 STPI\", fp_text(font.size = 12, bold = TRUE, font.family = \"Arial\", color = \"cornflowerblue\")),","      fp_p = fp_par(text.align = \"center\")","    )","  )","}","","","# pour remplir le document Word avec du texte et les flextables","ecriture \u003c- function(ft_notes, ft_sign, nom_prenom){","  return(","    doc %\u003e% ","    body_add_par(\"\") %\u003e%","    body_add_fpar(titre()) %\u003e%","    body_add_par(\"\") %\u003e%","    body_add_par(paste(\"Nom et prénom de l'étudiant·e :\", nom_prenom), style = \"Normal\") %\u003e%","    body_add_par(\"Semestre : ☐ 3    ☐ 4 \\t\\t Année complète : ☐\", style = \"Normal\") %\u003e%","    body_add_par(\"Motif de l'établissement du présent contrat :\", style = \"Normal\") %\u003e%","    body_add_par(\"\\t☐ SHN \\t☐ Redoublement \\t☐ Redoublement de Cas de Force Majeure\", style = \"Normal\") %\u003e%","    body_add_par(\"\\t☐ Autre (à préciser) : Dossier médical \", style = \"Normal\")%\u003e%","    body_add_par(\"\\tDétails du contrat d'études :\") %\u003e%","    body_add_par(\"\")%\u003e%","    body_add_flextable(value = ft_notes)%\u003e%","    body_add_par(\"\")%\u003e%","    body_add_par(\"\")%\u003e%","    body_add_par(\"L’élève s’engage à respecter le présent contrat signé.\", style=\"Normal\")%\u003e%","    body_add_par(\"\")%\u003e%","    body_add_flextable(value = ft_sign)","  )","}","","generation_df_notes \u003c- function(id){","  # récupération du fichier jury","  fichier_jury \u003c- \"./jury.xlsx\"","  ","  # récupération des notes de l'étudiant·e","  notes_S3 \u003c- notes_from_jury(fichier_jury, 2, col_S3, id)","  notes_S4 \u003c- notes_from_jury(fichier_jury, 3, col_S4, id)","  notes \u003c- c(notes_S3, notes_S4)","  ","  #récupération des validations des UE de l'étudiant·e","  val_S3 \u003c- valide_UE(fichier_jury, 2, col_val_S3, id)","  val_S4 \u003c- valide_UE(fichier_jury, 3, col_val_S4, id)","  val \u003c- c(val_S3, val_S4)","  ","  #pour mettre des croix si l'EC n'est pas validé ou n'a pas de note","  croix \u003c- croix(notes, val)","  ","  #création des dataframes","  notes_etudiant \u003c- build_notes_etudiant(notes, croix)","  ","  # Ajout dans la liste globale","  #notes_etudiants[[id]] \u003c- notes_etudiant","  ","  return (notes_etudiant)","}","","nom_prenom \u003c- function(id){","  fichier_jury \u003c- \"./jury.xlsx\"","  semestre \u003c- read_excel(fichier_jury, sheet = 2, col_types = \"text\")","  nom \u003c- semestre[semestre[[1]] == id, 2]","  nom \u003c- unlist(nom[rowSums(is.na(nom)) != ncol(nom), ])","  ","  prenom \u003c- semestre[semestre[[1]] == id, 3]","  prenom \u003c- unlist(prenom[rowSums(is.na(prenom)) != ncol(prenom), ])","","  return(paste(nom,prenom))","}","","","#génération du contrat","generation \u003c- function(id, doc, notes_etudiant = NULL){","  nom_prenom \u003c- nom_prenom(id)","  ","  # Si les notes modifiées ne sont pas fournies, on les génère normalement","  if (is.null(notes_etudiant)) {","    notes_etudiant \u003c- generation_df_notes(id)","  }","  ","  signature \u003c- build_signature()","  ","  #Création des flextables","  ft_notes \u003c- flextable(notes_etudiant)","  ft_sign \u003c- flextable(signature)","  ","  ft_notes \